 import './index.css'
import { useState } from 'react';
import { Link } from 'react-router-dom';

 export default function LoginPage(){
    const [email, setEmail] = useState('');
    const [password, setpassword] = useState('');
    const [error,setError] = useState(null);
    // const [passworddisplay, setpassworddisplay] = useState('');
    
    const handleSubmit = (e) =>{
        e.preventDefault();
        console.log(email);
        fetch('ar-project-server',{
            method: `POST`,
            headers: {"Content-Type": "application/json"},
            body: {email,password}
        }
        ).then(res =>{
            if(!res.ok){
                throw Error("This resource is un available");
            }
            return res.json();}
        ).then(() =>{ 
        setEmail('');
        setpassword('');}
    ).catch(err =>{
        if(err.name === 'AbortError'){
            console.log("Aborted")
        }
        else{
        setError(err.message);
        setIsLoading(false);
        }})
    }

    return (
        <div className='Login'>
            <h1 className='Title'>AR - SMART CAMPUS NAVIGATION</h1>
            <form className='LoginBox' onSubmit={handleSubmit}>
                <p>Enter your cedentials:</p>
                {/* <div className= 'padding'></div> */}
                    <div className='row'>
                        <label>Email:</label> <input className='TextField' placeholder='exapmle@Domain' value={email} onChange={e => setEmail(e.target.value)} required/>
                        <p>{email}</p>
                    </div>
                    <div className='row'>
                    <label>Password:</label> <input className='TextField' placeholder='password' value={password} onChange={e => setpassword(e.target.value)} required/>
                        <p>{password}</p>
                    </div>
                    {/* <spacer className='padding'></spacer> */}
                <Link to='./ForgotPasswordPage.js' className='forgotpassword'>forgot password?</Link>
                <div className= 'padding'></div>
                <div className='LoginButton'>
                    <button>Login</button>
                </div>
                <div className= 'padding'></div>
                <div>
                    <Link to='./register.js'>Don't have an account? Register</Link>
                </div>
                <Link to='./termsofServicePage.js'>Terms of service</Link>
            </form>
        </div>
    )
}

